select nome_pontoturistico as Ponto_Turistico ,avg(pontoturisticoavaliacao.nota) as Media, stddev(pontoturisticoavaliacao.nota) as Desvio_Padrao from pontoturistico inner join pontoturisticoavaliacao on (pontoturistico.idpontoturistico = pontoturisticoavaliacao.idpontoturistico) group by nome_pontoturistico having avg(pontoturisticoavaliacao.nota) - stddev(pontoturisticoavaliacao.nota) > 8 order by avg(pontoturisticoavaliacao.nota) desc
