SELECT USUARIO.NOME, PONTOTURISTICOAVALIACAO.NOTA, PONTOTURISTICOAVALIACAO.COMENTARIO, PONTOTURISTICOAVALIACAO.DT_AVALIACAO FROM USUARIO INNER JOIN TURISTA ON TURISTA.IDUSUARIO = USUARIO.IDUSUARIO INNER JOIN PONTOTURISTICOAVALIACAO ON PONTOTURISTICOAVALIACAO.IDTURISTA = TURISTA.IDTURISTA INNER JOIN PONTOTURISTICO ON PONTOTURISTICO.IDPONTOTURISTICO = PONTOTURISTICOAVALIACAO.IDPONTOTURISTICO WHERE DT_AVALIACAO >= CURRENT_DATE -10;