SELECT USUARIO.NOME, PONTOTURISTICO.NOME_PONTOTURISTICO, AGE(CURRENT_DATE, HISTORICOVISITA.DT_VISITA) FROM USUARIO INNER JOIN TURISTA ON USUARIO.IDUSUARIO = TURISTA.IDUSUARIO INNER JOIN HISTORICOVISITA ON HISTORICOVISITA.IDTURISTA = TURISTA.IDTURISTA INNER JOIN PONTOTURISTICO ON PONTOTURISTICO.IDPONTOTURISTICO = HISTORICOVISITA.IDPONTOTURISTICO ORDER BY AGE;