SELECT PONTOTURISTICO.NOME_PONTOTURISTICO, PONTOTURISTICO.TIPO_PONTOTURISTICO, PONTOTURISTICO.PUBLICO_ALVO, PONTOTURISTICO.TIPO_AMBIENTE, EVENTO.DT_EVENTO, NOW(),(EXTRACT('HOUR' FROM CURRENT_TIME) - EXTRACT('HOUR' FROM EVENTO.DT_EVENTO)) AS PROXIMO_EVENTO_HORAS FROM PONTOTURISTICO INNER JOIN EVENTO ON EVENTO.IDPONTOTURISTICO = PONTOTURISTICO.IDPONTOTURISTICO WHERE EVENTO.DT_EVENTO >= NOW() AND EVENTO.DT_EVENTO <= CURRENT_DATE + 1;