SELECT USUARIO.NOME, TURISTA.SEXO, PONTOTURISTICO.NOME_PONTOTURISTICO, PONTOTURISTICO.TIPO_PONTOTURISTICO, PONTOTURISTICO.PUBLICO_ALVO, PONTOTURISTICO.TIPO_AMBIENTE, HISTORICOVISITA.DT_VISITA FROM USUARIO INNER JOIN TURISTA ON USUARIO.IDUSUARIO = TURISTA.IDUSUARIO INNER JOIN HISTORICOVISITA ON HISTORICOVISITA.IDTURISTA = TURISTA.IDTURISTA INNER JOIN PONTOTURISTICO ON PONTOTURISTICO.IDPONTOTURISTICO = HISTORICOVISITA.IDPONTOTURISTICO ORDER BY DT_VISITA DESC;