select municipio as Municipio, count(nome_pontoturistico) as Quantidade_de_Pontos_Turisticos, count(evento.descricao_evento) as Quantidade_de_Eventos, avg(pontoturisticoavaliacao.nota) as Media_das_Avaliacoes_de_Usuario  from pontoturistico left outer join evento on (pontoturistico.idpontoturistico = evento.idpontoturistico) left outer join pontoturisticoavaliacao on (pontoturistico.idpontoturistico = pontoturisticoavaliacao.idpontoturistico) where nome_pontoturistico in( select nome_pontoturistico from pontoturistico where municipio like 'Vila Velha' or municipio like 'Cariacica' or municipio like 'Guarapari' or municipio like 'Serra' or municipio like 'Vitoria' or municipio like 'Viana' or municipio like 'Fund√£o') group by municipio order by count(nome_pontoturistico) desc;
