CREATE VIEW COMENTS_AVA AS SELECT USUARIO.NOME, PONTOTURISTICOAVALIACAO.NOTA, PONTOTURISTICOAVALIACAO.COMENTARIO FROM USUARIO INNER JOIN TURISTA ON TURISTA.IDUSUARIO = USUARIO.IDUSUARIO INNER JOIN PONTOTURISTICOAVALIACAO ON PONTOTURISTICOAVALIACAO.IDTURISTA = TURISTA.IDTURISTA INNER JOIN PONTOTURISTICO ON PONTOTURISTICO.IDPONTOTURISTICO = PONTOTURISTICOAVALIACAO.IDPONTOTURISTICO WHERE nome_pontoturistico = 'Praia Secreta';

SELECT *FROM COMENTS_AVA;