SELECT ONIBUS.NUMERO_LINHA, ONIBUS.NOME_LINHA FROM ONIBUS INNER JOIN ONIBUSPONTOTURISTICO ON ONIBUSPONTOTURISTICO.IDONIBUS = ONIBUS.IDONIBUS INNER JOIN PONTOTURISTICO ON PONTOTURISTICO.IDPONTOTURISTICO = ONIBUSPONTOTURISTICO.IDPONTOTURISTICO WHERE PONTOTURISTICO.NOME_PONTOTURISTICO = 'Convento da Penha';